e_uu = xcov(u, u, 'unbiased') - crosscov(u, u, 'unbiased');
e_uy = xcov(u, y, 'unbiased') - crosscov(u, y, 'unbiased');
e_yu = xcov(y, u, 'unbiased') - crosscov(y, u, 'unbiased');
e_yy = xcov(y, y, 'unbiased') - crosscov(y, y, 'unbiased');
dot(e_uu, e_uu)
dot(e_uy, e_uy)
dot(e_yu, e_yu)
dot(e_yy, e_yy)

