%% Question 2a
clear all
close all
clc

fs = 500;
dt = 1/fs;
T = 5;
t = 0:dt:T-dt;
f_sig = 8; % Hz

u = 2*sin(2*pi*f_sig*t) + normrnd(0, 1, size(t));

[B, A] = butter(2, 20/(fs/2));
y = filter(B, A, u);
h = plot(t, u, 'b', t, y, 'r');
    set(h(1),'linewidth',1);
    set(h(2),'linewidth',4);
Y = fft(y);
f = t/T*fs;
figure('Name', 'Question 2a')
subplot(2, 1, 1)
    plot(f, abs(Y), 'linewidth', 2)
    xlabel('frequency [Hz]', 'Fontsize', 18);
    ylabel('|Y(f)|', 'Fontsize', 18);
    set(gca, 'FontSize', 18)
subplot(2, 1, 2)
    plot(f, phase(Y), 'linewidth', 2)
    xlabel('frequency [Hz]', 'Fontsize', 18);
    ylabel('phase(Y(f)) [deg]', 'Fontsize', 18);
    set(gca, 'FontSize', 18)
eps_save('question2a')

